              fileName: 'circle.svg',
              file: '<svg><path fill="red" d="M50 50h150v50H50z"/></svg>'
            }
          }
        }
      }))
      .then((asset) => asset.processForAllLocales())
      .then((asset) => asset.publish())
      .catch(console.error)
